import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { ChatMessageDto } from '../models/chatMessageDto';

@Injectable({
  providedIn: 'root',
})
export class ChatService {
  constructor(private http: HttpClient) {}

  getChatMessages(): Observable<ChatMessageDto[]> {
    return this.http.get<ChatMessageDto[]>('http://localhost:8080/chat/messages');
  }

  sendMessage(chatMessageDto: ChatMessageDto): Observable<any> {
    return this.http.post('http://localhost:8080/chat/messages', chatMessageDto);
  }
}
