import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { ChatMessageDto } from "../models/chatMessageDto";

@Injectable({
  providedIn: 'root'
})
export class ChatService {

  private readonly chatUrl = 'http://localhost:4000/chat/postMessage';

  constructor(private http: HttpClient) { }

  public getChatMessages(): Observable<ChatMessageDto[]> {
    return this.http.get<ChatMessageDto[]>(this.chatUrl);
  }

  public sendMessage(chatMessageDto: ChatMessageDto): Observable<ChatMessageDto> {
    console.log(chatMessageDto);
    return this.http.post<ChatMessageDto>(this.chatUrl, chatMessageDto);
  }
}
